/**
 * Subscription tier enumeration
 * Represents different levels of service plans available
 */
export enum SubscriptionTier {
  /** Basic tier - Entry level subscription */
  Basic = 1,
  
  /** Pro tier - Professional subscription with enhanced features */
  Pro = 2,
  
  /** Master tier - Advanced subscription with premium features */
  Master = 3,
  
  /** Team tier - Collaborative subscription for teams */
  Team = 4
}