/**
 * Layout mode enumeration
 * Defines the available layout modes for the application
 */
export enum LayoutMode {
  /**
   * Layout design mode
   * Enables the design/editing interface for layouts
   */
  layoutDesignMode = 0
}

/**
 * Type alias for the LayoutMode enum
 * @deprecated Use LayoutMode directly instead
 */
export type LayoutModeType = LayoutMode;