/**
 * Default background image asset path
 * 
 * This module exports the URL path to the default background image asset.
 * The image is typically used as a fallback or initial background in the application.
 * 
 * @module DefaultBackgroundAsset
 */

/**
 * The relative path to the default background image asset.
 * 
 * Format: "assets/defaultbackground.94cdb606.jpg"
 * 
 * The hash (94cdb606) in the filename is used for cache busting to ensure
 * browsers load the latest version when the asset changes.
 * 
 * @type {string}
 */
declare const defaultBackgroundPath: string;

export default defaultBackgroundPath;