/**
 * TypeScript helper functions for compiled code.
 * These are runtime utilities used by the TypeScript compiler for various language features.
 * @packageDocumentation
 */

/**
 * Assigns properties from one or more source objects to a target object.
 * Equivalent to Object.assign().
 * @param target - The target object to assign properties to
 * @param sources - One or more source objects
 * @returns The target object with assigned properties
 */
export function __assign<T, U>(target: T, source: U): T & U;
export function __assign<T, U, V>(target: T, source1: U, source2: V): T & U & V;
export function __assign<T, U, V, W>(target: T, source1: U, source2: V, source3: W): T & U & V & W;
export function __assign(target: object, ...sources: any[]): any;

/**
 * Creates an async iterator delegator for generator delegation.
 * Used in async generator functions with yield*.
 * @param iterable - The async iterable to delegate to
 * @returns An async iterator that delegates to the provided iterable
 */
export function __asyncDelegator(iterable: AsyncIterable<any>): AsyncIterator<any>;

/**
 * Wraps an async generator function to provide proper async iteration protocol.
 * @param thisArg - The 'this' context for the generator
 * @param _arguments - Arguments to pass to the generator function
 * @param generator - The generator function to wrap
 * @returns An async iterable iterator
 */
export function __asyncGenerator(thisArg: any, _arguments: any, generator: Function): AsyncIterableIterator<any>;

/**
 * Converts a sync or async iterable into an async iterable.
 * Used for 'for await...of' loops.
 * @param iterable - The iterable to convert
 * @returns An async iterable iterator
 */
export function __asyncValues<T>(iterable: Iterable<T> | AsyncIterable<T>): AsyncIterableIterator<T>;

/**
 * Wraps a value to mark it as awaitable in async generators.
 * @param value - The value to wrap
 * @returns A wrapped awaitable value
 */
export function __await<T>(value: T): T;

/**
 * Wraps async functions to handle promises and generator-based async control flow.
 * @param thisArg - The 'this' context for the async function
 * @param _arguments - Arguments passed to the async function
 * @param PromiseConstructor - The Promise constructor to use
 * @param generator - The generator function representing the async function body
 * @returns A promise that resolves with the async function result
 */
export function __awaiter<T = any>(
  thisArg: any,
  _arguments: any,
  PromiseConstructor: PromiseConstructorLike,
  generator: (...args: any[]) => Iterator<any, T>
): Promise<T>;

/**
 * Reads a private field value using WeakMap or accessor-based private fields.
 * @param receiver - The object instance to read from
 * @param state - The WeakMap or accessor object storing the private field
 * @param kind - The kind of private member: "f" (field), "a" (accessor), "m" (method)
 * @param f - The getter function for accessor fields
 * @returns The private field value
 * @throws TypeError if the private field was not declared on the receiver's class
 */
export function __classPrivateFieldGet<T>(
  receiver: object,
  state: { has(o: object): boolean; get(o: object): T | undefined },
  kind: "f" | "a" | "m",
  f?: () => T
): T;

/**
 * Checks if a private field exists on an object.
 * Used for the 'in' operator with private fields.
 * @param state - The WeakMap or class storing the private field
 * @param receiver - The object to check
 * @returns True if the private field exists on the receiver
 * @throws TypeError if receiver is not an object
 */
export function __classPrivateFieldIn(
  state: Function | { has(o: object): boolean },
  receiver: object
): boolean;

/**
 * Sets a private field value using WeakMap or accessor-based private fields.
 * @param receiver - The object instance to write to
 * @param state - The WeakMap or accessor object storing the private field
 * @param value - The value to set
 * @param kind - The kind of private member: "f" (field), "a" (accessor), "m" (method)
 * @param f - The setter function for accessor fields
 * @returns The value that was set
 * @throws TypeError if the private field was not declared on the receiver's class or is read-only
 */
export function __classPrivateFieldSet<T>(
  receiver: object,
  state: { has(o: object): boolean; set(o: object, value: T): void },
  value: T,
  kind: "f" | "a" | "m",
  f?: (value: T) => void
): T;

/**
 * Creates a property binding for re-exported modules.
 * Used for ES module interop.
 * @param target - The target object to bind the property to
 * @param source - The source object containing the property
 * @param key - The property key to bind
 * @param desc - Optional property descriptor key (defaults to key)
 */
export function __createBinding(
  target: object,
  source: object,
  key: PropertyKey,
  desc?: PropertyKey
): void;

/**
 * Applies decorators to a class member or class declaration.
 * @param decorators - Array of decorator functions
 * @param target - The target object (class prototype for members, constructor for classes)
 * @param key - The property key (for member decorators)
 * @param desc - The property descriptor (for member decorators)
 * @returns The modified descriptor or undefined
 */
export function __decorate(
  decorators: Function[],
  target: object,
  key?: PropertyKey,
  desc?: PropertyDescriptor | null
): PropertyDescriptor | void;

/**
 * Re-exports all properties from a module except 'default'.
 * @param source - The source module to export from
 * @param target - The target module to export to
 */
export function __exportStar(source: object, target: object): void;

/**
 * Sets up prototype chain inheritance for class extends.
 * @param derived - The derived (child) class constructor
 * @param base - The base (parent) class constructor
 * @throws TypeError if base is not a constructor or null
 */
export function __extends(derived: Function, base: Function): void;

/**
 * Creates a generator state machine for generator functions.
 * @param thisArg - The 'this' context for the generator
 * @param body - The generator body function containing yield expressions
 * @returns An iterator for the generator
 */
export function __generator(thisArg: any, body: Function): Iterator<any>;

/**
 * Wraps a module with __esModule marker for ES module interop.
 * @param mod - The module to wrap
 * @returns The module or an object with 'default' property
 */
export function __importDefault<T>(mod: T): T extends { __esModule: true } ? T : { default: T };

/**
 * Imports all exports from a module as a namespace object.
 * @param mod - The module to import from
 * @returns A namespace object containing all exports
 */
export function __importStar<T>(mod: T): T;

/**
 * Creates a template object for tagged template literals.
 * @param cooked - The cooked (processed) string array
 * @param raw - The raw (unprocessed) string array
 * @returns The template object with 'raw' property
 */
export function __makeTemplateObject(cooked: TemplateStringsArray, raw: TemplateStringsArray): TemplateStringsArray;

/**
 * Decorator factory for emitting type metadata.
 * Requires Reflect.metadata to be available.
 * @param metadataKey - The metadata key
 * @param metadataValue - The metadata value
 * @returns A decorator function
 */
export function __metadata(metadataKey: any, metadataValue: any): (target: Function) => void;

/**
 * Creates a parameter decorator.
 * @param paramIndex - The index of the parameter
 * @param decorator - The decorator function
 * @returns A decorator that applies to the parameter at the specified index
 */
export function __param(paramIndex: number, decorator: Function): (target: object, propertyKey: PropertyKey) => void;

/**
 * Converts an iterable or array-like object to an array with a specific length.
 * Used for destructuring assignments.
 * @param iterable - The iterable to read from
 * @param count - Optional maximum number of elements to read
 * @returns An array of elements
 */
export function __read<T>(iterable: Iterable<T>, count?: number): T[];

/**
 * Extracts remaining properties after destructuring (object rest).
 * @param source - The source object
 * @param excluded - Array of property keys to exclude
 * @returns A new object with the remaining properties
 */
export function __rest<T, K extends keyof T>(source: T, excluded: K[]): Omit<T, K>;

/**
 * Spreads arrays into a single array (legacy).
 * @deprecated Use native spread syntax or __spreadArray instead
 * @param arrays - Arrays to spread
 * @returns A new array containing all elements
 */
export function __spread<T>(...arrays: (T[] | readonly T[])[]): T[];

/**
 * Concatenates arrays with optional spreading (modern).
 * @param target - The target array
 * @param source - The source array to spread
 * @param pack - Whether to pack remaining arguments
 * @returns A new array with concatenated elements
 */
export function __spreadArray<T>(target: T[], source: readonly T[], pack?: boolean): T[];

/**
 * Spreads multiple arrays into a single array (legacy).
 * @deprecated Use native spread syntax or __spreadArray instead
 * @param arrays - Arrays to spread
 * @returns A new array containing all elements
 */
export function __spreadArrays<T>(...arrays: (T[] | readonly T[])[]): T[];

/**
 * Creates an iterator from an iterable or array-like object.
 * @param iterable - The iterable to create an iterator for
 * @returns An iterator
 * @throws TypeError if the object is not iterable
 */
export function __values<T>(iterable: Iterable<T>): Iterator<T>;