/**
 * Babylon.js Materials Library - Type Definitions
 * 
 * This module provides type definitions for the Babylon.js materials library,
 * including custom materials, procedural materials, and shader effects.
 */

// ============================================================================
// Custom Material System
// ============================================================================

/**
 * Re-exported custom material components and utilities.
 * Includes CustomMaterial and PBRCustomMaterial for extending base materials.
 */
export * from './customparts';

/**
 * Additional custom material exports.
 * Provides advanced customization options for material systems.
 */
export * from './customparts';

// ============================================================================
// Material Modules (Available but not directly exported)
// ============================================================================

/**
 * Fur Material - Simulates realistic fur and hair rendering
 * @module lts/materials/dist/fur
 */

/**
 * Sky Material - Procedural sky rendering with atmospheric effects
 * @module lts/materials/dist/sky
 */

/**
 * Water Material - Realistic water rendering with reflections and refractions
 * @module lts/materials/dist/water
 */

/**
 * Fire Material - Animated fire and flame effects
 * @module lts/materials/dist/fire
 */

/**
 * Lava Material - Molten lava rendering with heat distortion
 * @module lts/materials/dist/lava
 */

/**
 * Grid Material - Procedural grid patterns for debugging and visualization
 * @module lts/materials/dist/grid
 */

/**
 * Gradient Material - Smooth color gradient rendering
 * @module lts/materials/dist/gradient
 */

/**
 * Cell Material - Toon/cel-shading effects
 * @module lts/materials/dist/cell
 */

/**
 * Simple Material - Lightweight basic material
 * @module lts/materials/dist/simple
 */

/**
 * Normal Material - Material for visualizing surface normals
 * @module lts/materials/dist/normal
 */

/**
 * Shadow Only Material - Renders only shadows, transparent otherwise
 * @module lts/materials/dist/shadowOnly
 */

/**
 * Tri-Planar Material - Tri-planar texture mapping for complex geometry
 * @module lts/materials/dist/triPlanar
 */

/**
 * Mix Material - Blends multiple materials together
 * @module lts/materials/dist/mix
 */

/**
 * Terrain Material - Multi-texture terrain rendering
 * @module lts/materials/dist/terrain
 */

/**
 * Legacy compatibility layer for older material systems
 * @module lts/materials/dist/legacy
 */