/**
 * TypeScript helper functions for supporting various language features.
 * These utilities are used by the TypeScript compiler to implement features
 * like inheritance, async/await, decorators, and more.
 * @module tslib
 */

/**
 * Assigns properties from one or more source objects to a target object.
 * @param target - The target object to assign properties to
 * @param sources - One or more source objects
 * @returns The target object with assigned properties
 */
export function __assign<T, U>(target: T, source: U): T & U;
export function __assign<T, U, V>(target: T, source1: U, source2: V): T & U & V;
export function __assign<T, U, V, W>(target: T, source1: U, source2: V, source3: W): T & U & V & W;
export function __assign(target: object, ...sources: any[]): any;

/**
 * Creates an async iterator delegate that wraps another async iterator.
 * @param iterable - The iterable to delegate to
 * @returns An async iterator delegate
 */
export function __asyncDelegator(iterable: AsyncIterable<any>): AsyncIterator<any>;

/**
 * Creates an async generator wrapper for implementing async generator functions.
 * @param thisArg - The 'this' context for the generator
 * @param args - Arguments passed to the generator function
 * @param generator - The generator function to wrap
 * @returns An async iterable iterator
 */
export function __asyncGenerator(
  thisArg: any,
  args: any[] | undefined,
  generator: Function
): AsyncIterableIterator<any>;

/**
 * Converts an object into an async iterator.
 * @param iterable - The object to convert (must have Symbol.asyncIterator or Symbol.iterator)
 * @returns An async iterator
 * @throws {TypeError} If Symbol.asyncIterator is not defined
 */
export function __asyncValues<T>(iterable: AsyncIterable<T> | Iterable<T>): AsyncIterableIterator<T>;

/**
 * Wraps a value to be awaited in an async function.
 * @param value - The value to await
 * @returns A wrapped awaitable value
 */
export function __await<T>(value: T): T;

/**
 * Implements async/await functionality for async functions.
 * @param thisArg - The 'this' context for the async function
 * @param args - Arguments passed to the async function
 * @param PromiseConstructor - The Promise constructor to use
 * @param generator - The generator function representing the async function body
 * @returns A promise that resolves with the async function result
 */
export function __awaiter<T = any>(
  thisArg: any,
  args: any[] | undefined,
  PromiseConstructor: PromiseConstructorLike,
  generator: Function
): Promise<T>;

/**
 * Gets the value of a private class field.
 * @param receiver - The object instance containing the private field
 * @param state - The WeakMap or private field storage
 * @param kind - The kind of private member ('f' for field, 'a' for accessor, 'm' for method)
 * @param f - The accessor function or field descriptor
 * @returns The value of the private field
 * @throws {TypeError} If the private field doesn't exist on the object
 */
export function __classPrivateFieldGet<T>(
  receiver: any,
  state: any,
  kind: 'f' | 'a' | 'm',
  f?: any
): T;

/**
 * Checks if an object has a private field.
 * @param state - The private field identifier or WeakMap
 * @param receiver - The object to check
 * @returns True if the object has the private field
 * @throws {TypeError} If receiver is not an object
 */
export function __classPrivateFieldIn(state: any, receiver: any): boolean;

/**
 * Sets the value of a private class field.
 * @param receiver - The object instance containing the private field
 * @param state - The WeakMap or private field storage
 * @param value - The value to set
 * @param kind - The kind of private member ('f' for field, 'a' for accessor, 'm' for method)
 * @param f - The accessor function or field descriptor
 * @returns The set value
 * @throws {TypeError} If the private field doesn't exist or is read-only
 */
export function __classPrivateFieldSet<T>(
  receiver: any,
  state: any,
  value: T,
  kind: 'f' | 'a' | 'm',
  f?: any
): T;

/**
 * Creates a binding for re-exporting a named export from another module.
 * @param target - The target object to bind to
 * @param source - The source object to bind from
 * @param key - The property key to bind
 * @param key2 - Optional alternative property key in target (defaults to key)
 */
export function __createBinding(
  target: object,
  source: object,
  key: PropertyKey,
  key2?: PropertyKey
): void;

/**
 * Applies decorators to a class, method, accessor, or property.
 * @param decorators - Array of decorator functions
 * @param target - The target class or prototype
 * @param key - The property key (for member decorators)
 * @param descriptor - The property descriptor (for member decorators)
 * @returns The modified descriptor or class
 */
export function __decorate(
  decorators: Function[],
  target: any,
  key?: string | symbol,
  descriptor?: PropertyDescriptor | null
): any;

/**
 * Re-exports all named exports from a module.
 * @param source - The source module to export from
 * @param target - The target module to export to
 */
export function __exportStar(source: object, target: object): void;

/**
 * Implements class inheritance by setting up the prototype chain.
 * @param derived - The derived (child) class constructor
 * @param base - The base (parent) class constructor
 * @throws {TypeError} If base is not a constructor or null
 */
export function __extends(derived: Function, base: Function): void;

/**
 * Implements generator function state machine.
 * @param thisArg - The 'this' context for the generator
 * @param body - The generator body function containing the state machine
 * @returns A generator iterator
 */
export function __generator(thisArg: any, body: Function): Generator<any, any, any>;

/**
 * Wraps a module with a default export.
 * @param module - The module to wrap
 * @returns The module with a default export if it's an ES module, or wrapped as default
 */
export function __importDefault<T>(module: T): T | { default: T };

/**
 * Imports all exports from a module as a namespace object.
 * @param module - The module to import from
 * @returns A namespace object containing all exports
 */
export function __importStar<T>(module: T): T;

/**
 * Creates a template object for tagged template literals.
 * @param cooked - The cooked (processed) template strings
 * @param raw - The raw (unprocessed) template strings
 * @returns The template object with raw property
 */
export function __makeTemplateObject(
  cooked: TemplateStringsArray,
  raw: TemplateStringsArray
): TemplateStringsArray;

/**
 * Emits metadata for reflection when using decorators.
 * @param metadataKey - The metadata key
 * @param metadataValue - The metadata value
 * @returns A decorator function or undefined
 */
export function __metadata(metadataKey: any, metadataValue: any): Function | undefined;

/**
 * Creates a parameter decorator.
 * @param parameterIndex - The index of the parameter
 * @param decorator - The decorator function to apply
 * @returns A parameter decorator function
 */
export function __param(parameterIndex: number, decorator: Function): Function;

/**
 * Converts an array-like object or iterator into an array with a specified length.
 * @param iterable - The iterable to convert
 * @param length - Optional maximum number of elements to read
 * @returns An array containing the elements
 */
export function __read<T>(iterable: Iterable<T>, length?: number): T[];

/**
 * Extracts remaining properties from an object (rest properties in object destructuring).
 * @param source - The source object
 * @param excludedKeys - Array of keys to exclude from the result
 * @returns A new object with all properties except the excluded ones
 */
export function __rest<T extends object, K extends keyof T>(
  source: T,
  excludedKeys: K[]
): Omit<T, K>;

/**
 * Spreads multiple arrays into a single array (deprecated, use __spreadArray instead).
 * @param arrays - Arrays to spread
 * @returns A new array containing all elements
 * @deprecated Use __spreadArray instead
 */
export function __spread<T>(...arrays: (T[] | readonly T[])[]): T[];

/**
 * Concatenates an array with elements from another array or iterable.
 * @param target - The target array to concatenate to
 * @param source - The source array or iterable to concatenate
 * @param pack - Whether to pack the source into an array if needed
 * @returns A new array with concatenated elements
 */
export function __spreadArray<T, U>(
  target: readonly T[],
  source: readonly U[],
  pack?: boolean
): (T | U)[];

/**
 * Spreads multiple arrays into a single array (deprecated, use __spreadArray instead).
 * @param arrays - Arrays to spread
 * @returns A new array containing all elements from all input arrays
 * @deprecated Use __spreadArray instead
 */
export function __spreadArrays<T>(...arrays: (T[] | readonly T[])[]): T[];

/**
 * Converts an iterable object into an iterator.
 * @param iterable - The object to iterate (must have Symbol.iterator or be array-like)
 * @returns An iterator for the object
 * @throws {TypeError} If the object is not iterable
 */
export function __values<T>(iterable: Iterable<T>): Iterator<T>;