/**
 * TypeScript helper functions for runtime support of various language features.
 * These utilities enable advanced TypeScript features in compiled JavaScript.
 * @packageDocumentation
 */

/**
 * Assigns properties from one or more source objects to a target object.
 * Equivalent to Object.assign() or the spread operator {...obj}.
 * @param target - The target object to copy properties to
 * @param sources - One or more source objects to copy properties from
 * @returns The target object with copied properties
 */
export function __assign<T, U>(target: T, ...sources: U[]): T & U;

/**
 * Creates an async iterator delegate for generator delegation with `yield*`.
 * @param iterable - The async iterable to delegate to
 * @returns An async iterator with next, throw, and return methods
 */
export function __asyncDelegator<T>(iterable: AsyncIterator<T>): AsyncIterator<T>;

/**
 * Wraps an async generator function to return an async iterator.
 * @param thisArg - The `this` context for the generator
 * @param _arguments - Arguments to pass to the generator function
 * @param generator - The generator function to wrap
 * @returns An async iterator that yields values from the generator
 */
export function __asyncGenerator<T>(
  thisArg: unknown,
  _arguments: unknown,
  generator: Function
): AsyncIterator<T>;

/**
 * Creates an async iterator from an async iterable or iterable object.
 * @param iterable - The object to create an async iterator from
 * @returns An async iterator for the given iterable
 */
export function __asyncValues<T>(iterable: AsyncIterable<T> | Iterable<T>): AsyncIterator<T>;

/**
 * Wraps a value for use with `await` in async generators.
 * @param value - The value to await
 * @returns A wrapped awaitable value
 */
export function __await<T>(value: T): T;

/**
 * Wraps an async function to return a promise, enabling async/await syntax.
 * @param thisArg - The `this` context for the function
 * @param _arguments - Arguments to pass to the function
 * @param P - Promise constructor (defaults to global Promise)
 * @param generator - The generator function representing the async function body
 * @returns A promise that resolves when the async function completes
 */
export function __awaiter<T>(
  thisArg: unknown,
  _arguments: unknown,
  P: PromiseConstructor,
  generator: () => Generator<unknown, T, unknown>
): Promise<T>;

/**
 * Gets the value of a class private field (# syntax).
 * @param receiver - The object instance containing the private field
 * @param state - The WeakMap or private field storage
 * @param kind - The kind of private member: "f" (field), "a" (accessor), "m" (method)
 * @param f - The getter function for accessor properties
 * @returns The value of the private field
 * @throws TypeError if the private field doesn't exist on the receiver
 */
export function __classPrivateFieldGet<T, V>(
  receiver: T,
  state: WeakMap<T, V> | { has(o: T): boolean; get(o: T): V },
  kind: "f" | "a" | "m",
  f?: () => V
): V;

/**
 * Checks if an object has a private field (for `#field in obj` syntax).
 * @param state - The WeakMap or function representing the private field
 * @param receiver - The object to check for the private field
 * @returns True if the receiver has the private field
 * @throws TypeError if receiver is not an object
 */
export function __classPrivateFieldIn<T>(
  state: WeakMap<T, unknown> | (new (...args: unknown[]) => T),
  receiver: unknown
): receiver is T;

/**
 * Sets the value of a class private field (# syntax).
 * @param receiver - The object instance containing the private field
 * @param state - The WeakMap or private field storage
 * @param value - The value to set
 * @param kind - The kind of private member: "f" (field), "a" (accessor), "m" (method)
 * @param f - The setter function for accessor properties
 * @returns The value that was set
 * @throws TypeError if the private field doesn't exist or is read-only
 */
export function __classPrivateFieldSet<T, V>(
  receiver: T,
  state: WeakMap<T, V> | { has(o: T): boolean; set(o: T, value: V): void },
  value: V,
  kind: "f" | "a" | "m",
  f?: (v: V) => void
): V;

/**
 * Creates a property binding for re-exported module members.
 * @param target - The target object to bind the property to
 * @param source - The source object to bind the property from
 * @param key - The property key to bind
 * @param alias - Optional alias name for the bound property (defaults to key)
 */
export function __createBinding(
  target: object,
  source: object,
  key: PropertyKey,
  alias?: PropertyKey
): void;

/**
 * Applies decorators to a class, method, accessor, or property.
 * @param decorators - Array of decorator functions
 * @param target - The target class or prototype
 * @param propertyKey - The property name (for member decorators)
 * @param descriptor - The property descriptor (for member decorators)
 * @returns The decorated descriptor or class
 */
export function __decorate(
  decorators: Function[],
  target: object,
  propertyKey?: string | symbol,
  descriptor?: PropertyDescriptor | null
): PropertyDescriptor | Function;

/**
 * Exports all properties from a source module to a target module.
 * @param source - The source module to export from
 * @param target - The target module to export to
 */
export function __exportStar(source: object, target: object): void;

/**
 * Sets up prototype chain inheritance for class extension.
 * @param derived - The derived (child) class
 * @param base - The base (parent) class
 * @throws TypeError if base is not a constructor or null
 */
export function __extends(derived: Function, base: Function | null): void;

/**
 * Implements generator function runtime behavior for function* syntax.
 * @param thisArg - The `this` context for the generator
 * @param body - Function containing the generator state machine
 * @returns An iterator with next, throw, and return methods
 */
export function __generator<T>(
  thisArg: unknown,
  body: (state: unknown) => IteratorResult<T>
): Generator<T>;

/**
 * Wraps a module with a default export for interop with CommonJS.
 * @param module - The module to wrap
 * @returns The module if it has __esModule, otherwise wrapped with default property
 */
export function __importDefault<T>(module: T): T extends { __esModule: true } ? T : { default: T };

/**
 * Wraps a namespace import for interop with CommonJS modules.
 * @param module - The module to import as namespace
 * @returns The module with all properties if __esModule is true, otherwise a namespace object
 */
export function __importStar<T>(module: T): T;

/**
 * Creates a template object for tagged template literals.
 * @param cooked - Array of cooked (processed) string values
 * @param raw - Array of raw (unprocessed) string values
 * @returns Template object with cooked values and a raw property
 */
export function __makeTemplateObject(
  cooked: TemplateStringsArray,
  raw: TemplateStringsArray
): TemplateStringsArray;

/**
 * Attaches metadata using the Reflect Metadata API (if available).
 * @param metadataKey - The key for the metadata
 * @param metadataValue - The value of the metadata
 * @returns A decorator function that applies the metadata
 */
export function __metadata(metadataKey: unknown, metadataValue: unknown): Function;

/**
 * Creates a parameter decorator.
 * @param paramIndex - The index of the parameter
 * @param decorator - The decorator function to apply
 * @returns A decorator that applies to the specified parameter
 */
export function __param(paramIndex: number, decorator: Function): Function;

/**
 * Converts an iterable to an array with optional maximum length.
 * Used for destructuring assignments and spread operations.
 * @param iterable - The iterable to convert
 * @param maxLength - Optional maximum number of elements to read
 * @returns An array of values from the iterable
 */
export function __read<T>(iterable: Iterable<T>, maxLength?: number): T[];

/**
 * Extracts remaining properties from an object, excluding specified keys.
 * Implements the rest operator for object destructuring.
 * @param source - The source object
 * @param excludedKeys - Array of keys to exclude from the result
 * @returns A new object with all properties except the excluded keys
 */
export function __rest<T, K extends keyof T>(
  source: T,
  excludedKeys: K[]
): Omit<T, K>;

/**
 * Concatenates multiple arrays into one (legacy spread operator helper).
 * @deprecated Use native spread operator or __spreadArray instead
 * @param arrays - Arrays to concatenate
 * @returns A new array containing all elements
 */
export function __spread<T>(...arrays: (T | T[])[]): T[];

/**
 * Concatenates arrays with optional spread behavior.
 * @param target - The target array to prepend
 * @param source - The source array to append
 * @param pack - Whether to pack the result (for ...spread syntax)
 * @returns A new array with concatenated elements
 */
export function __spreadArray<T>(target: T[], source: T[], pack?: boolean): T[];

/**
 * Concatenates multiple arrays into one (legacy helper).
 * @deprecated Use __spreadArray instead
 * @param arrays - Arrays to concatenate
 * @returns A new array containing all elements from all input arrays
 */
export function __spreadArrays<T>(...arrays: T[][]): T[];

/**
 * Creates an iterator from an iterable object or array.
 * @param iterable - The object to create an iterator from
 * @returns An iterator for the given iterable
 * @throws TypeError if the object is not iterable
 */
export function __values<T>(iterable: Iterable<T>): Iterator<T>;