/**
 * TypeScript helper functions for supporting various language features and transformations.
 * These utilities are used by the TypeScript compiler to emit backward-compatible JavaScript code.
 * @module tslib
 */

/**
 * Assigns properties from one or more source objects to a target object.
 * Equivalent to Object.assign() but provides compatibility for older environments.
 * @param target - The target object to assign properties to
 * @param sources - One or more source objects
 * @returns The modified target object
 */
export function __assign<T, U>(target: T, source: U): T & U;
export function __assign<T, U, V>(target: T, source1: U, source2: V): T & U & V;
export function __assign<T, U, V, W>(target: T, source1: U, source2: V, source3: W): T & U & V & W;
export function __assign(target: object, ...sources: any[]): any;

/**
 * Creates an async iterator delegator for async generator functions.
 * Wraps an async iterator and delegates next, throw, and return operations.
 * @param iterable - The async iterable to delegate to
 * @returns An async iterator that delegates to the input iterable
 */
export function __asyncDelegator(iterable: AsyncIterator<any>): AsyncIterator<any>;

/**
 * Implements async generator functionality for environments without native support.
 * Manages the state machine for async generators with promise-based iteration.
 * @param thisArg - The 'this' context for the generator
 * @param _arguments - Arguments array to pass to the generator
 * @param generator - The generator function implementation
 * @returns An async iterator
 */
export function __asyncGenerator(
  thisArg: any,
  _arguments: any[] | undefined,
  generator: Function
): AsyncIterator<any>;

/**
 * Converts an object into an async iterable using Symbol.asyncIterator.
 * Provides async iteration support for objects that may not natively support it.
 * @param iterable - The object to convert to an async iterable
 * @returns An async iterator
 */
export function __asyncValues<T>(iterable: Iterable<T> | AsyncIterable<T>): AsyncIterator<T>;

/**
 * Wraps a value for use in async generator functions.
 * Marks a value as an awaitable result in async operations.
 * @param value - The value to wrap
 * @returns A wrapper object containing the value
 */
export function __await<T>(value: T): { v: T };

/**
 * Implements async/await functionality for environments without native support.
 * Wraps async functions to return promises and handle await expressions.
 * @param thisArg - The 'this' context for the async function
 * @param _arguments - Arguments array to pass to the function
 * @param P - Promise constructor (defaults to global Promise)
 * @param generator - The generator function representing the async function body
 * @returns A promise that resolves with the async function result
 */
export function __awaiter<T = any>(
  thisArg: any,
  _arguments: any[] | undefined,
  P: PromiseConstructor | undefined,
  generator: () => Iterator<any>
): Promise<T>;

/**
 * Gets the value of a private class field (WeakMap or getter-based).
 * Validates access rights and retrieves the private field value.
 * @param receiver - The object instance containing the private field
 * @param state - The WeakMap or state object holding private data
 * @param kind - The kind of accessor: "f" (field), "a" (accessor), "m" (method)
 * @param f - The getter function or field descriptor
 * @returns The private field value
 * @throws TypeError if the private member doesn't exist on the object
 */
export function __classPrivateFieldGet<T, V>(
  receiver: T,
  state: { has(obj: T): boolean; get(obj: T): V },
  kind: "f" | "a" | "m",
  f?: () => V
): V;

/**
 * Checks if a private field exists on an object.
 * Used to implement the 'in' operator for private class fields.
 * @param state - The private field identifier (WeakMap or function)
 * @param receiver - The object to check
 * @returns True if the private field exists on the object
 * @throws TypeError if receiver is not an object
 */
export function __classPrivateFieldIn<T>(
  state: { has(obj: T): boolean } | ((obj: T) => boolean),
  receiver: T
): boolean;

/**
 * Sets the value of a private class field (WeakMap or setter-based).
 * Validates access rights and updates the private field value.
 * @param receiver - The object instance containing the private field
 * @param state - The WeakMap or state object holding private data
 * @param value - The new value to set
 * @param kind - The kind of accessor: "f" (field), "a" (accessor), "m" (method)
 * @param f - The setter function or field descriptor
 * @returns The value that was set
 * @throws TypeError if the private member is read-only or doesn't exist
 */
export function __classPrivateFieldSet<T, V>(
  receiver: T,
  state: { has(obj: T): boolean; set(obj: T, value: V): void },
  value: V,
  kind: "f" | "a" | "m",
  f?: (value: V) => void
): V;

/**
 * Creates a binding for a re-exported module member.
 * Used for ES module interop when re-exporting named exports.
 * @param target - The target object to bind the property to
 * @param source - The source object containing the property
 * @param key - The property key to bind
 * @param alias - Optional alias name (defaults to key)
 */
export function __createBinding(
  target: object,
  source: object,
  key: PropertyKey,
  alias?: PropertyKey
): void;

/**
 * Applies decorators to a class, method, accessor, or property.
 * Executes decorators in reverse order and applies the result.
 * @param decorators - Array of decorator functions
 * @param target - The target class or prototype
 * @param key - The property key (for member decorators)
 * @param desc - The property descriptor (for member decorators)
 * @returns The modified descriptor or class
 */
export function __decorate(
  decorators: Function[],
  target: any,
  key?: string | symbol,
  desc?: PropertyDescriptor | null
): any;

/**
 * Re-exports all members from a source module to a target object.
 * Copies all enumerable properties except 'default'.
 * @param source - The source module to export from
 * @param target - The target object to export to
 */
export function __exportStar(source: object, target: object): void;

/**
 * Implements class inheritance by setting up the prototype chain.
 * Emulates ES6 class extends for ES5 environments.
 * @param derived - The derived (child) class constructor
 * @param base - The base (parent) class constructor
 * @throws TypeError if base is not a constructor or null
 */
export function __extends(derived: Function, base: Function): void;

/**
 * Implements generator function state machine for ES5 environments.
 * Manages generator execution, yielding, and control flow.
 * @param thisArg - The 'this' context for the generator
 * @param body - The generator body function containing the state machine
 * @returns A generator iterator
 */
export function __generator(thisArg: any, body: Function): Iterator<any>;

/**
 * Wraps a module with a default export for CommonJS interop.
 * Ensures proper handling of ES modules imported in CommonJS.
 * @param module - The module to wrap
 * @returns The module or a wrapper with a default property
 */
export function __importDefault<T>(module: T): T | { default: T };

/**
 * Imports an entire module as a namespace object.
 * Handles both ES modules and CommonJS modules.
 * @param module - The module to import
 * @returns A namespace object containing all exports
 */
export function __importStar<T>(module: T): T;

/**
 * Creates a template object for tagged template literals.
 * Attaches the raw strings array as a non-enumerable property.
 * @param cooked - The processed template strings
 * @param raw - The raw template strings
 * @returns The template object with raw property
 */
export function __makeTemplateObject(
  cooked: TemplateStringsArray,
  raw: TemplateStringsArray
): TemplateStringsArray;

/**
 * Decorator helper for emitting type metadata.
 * Used with reflect-metadata library for runtime type information.
 * @param metadataKey - The metadata key
 * @param metadataValue - The metadata value
 * @returns A decorator function or undefined
 */
export function __metadata(metadataKey: string, metadataValue: any): Function | undefined;

/**
 * Creates a parameter decorator factory.
 * Binds the parameter index to the decorator function.
 * @param paramIndex - The zero-based parameter index
 * @param decorator - The decorator function
 * @returns A parameter decorator
 */
export function __param(paramIndex: number, decorator: Function): ParameterDecorator;

/**
 * Reads values from an iterator or array-like object into an array.
 * Supports limiting the number of elements read.
 * @param iterable - The iterable or array-like object
 * @param count - Optional maximum number of elements to read
 * @returns An array of values
 * @throws TypeError if the object is not iterable
 */
export function __read<T>(iterable: Iterable<T>, count?: number): T[];

/**
 * Excludes specified properties from an object (object rest spread).
 * Creates a shallow copy without the excluded keys.
 * @param source - The source object
 * @param excludedKeys - Array of property keys to exclude
 * @returns A new object without the excluded properties
 */
export function __rest<T, K extends keyof T>(
  source: T,
  excludedKeys: K[]
): Omit<T, K>;

/**
 * Spreads multiple arrays or iterables into a single array (deprecated).
 * Use __spreadArray for modern code.
 * @param arrays - Arrays or iterables to spread
 * @returns A flattened array
 * @deprecated Use __spreadArray instead
 */
export function __spread<T>(...arrays: (T[] | Iterable<T>)[]): T[];

/**
 * Concatenates arrays with optional spreading of iterable elements.
 * Provides efficient array concatenation with spread support.
 * @param target - The target array to concatenate to
 * @param source - The source array or iterable
 * @param pack - Whether to spread the source elements
 * @returns The concatenated array
 */
export function __spreadArray<T>(target: T[], source: T[] | Iterable<T>, pack?: boolean): T[];

/**
 * Concatenates multiple arrays into a single array (deprecated).
 * Use __spreadArray for modern code.
 * @param arrays - Arrays to concatenate
 * @returns A flattened array
 * @deprecated Use __spreadArray instead
 */
export function __spreadArrays<T>(...arrays: T[][]): T[];

/**
 * Creates an iterator from an iterable or array-like object.
 * Provides Symbol.iterator support for non-iterable objects.
 * @param iterable - The object to iterate
 * @returns An iterator
 * @throws TypeError if the object is not iterable
 */
export function __values<T>(iterable: Iterable<T> | ArrayLike<T>): Iterator<T>;