/**
 * TypeScript helper functions for advanced language features.
 * These utilities support decorators, async operations, class inheritance, and iterators.
 * @packageDocumentation
 */

/**
 * Assigns properties from one or more source objects to a target object.
 * @param target - The target object to assign properties to
 * @param sources - One or more source objects
 * @returns The target object with assigned properties
 */
export declare function __assign<T, U>(target: T, source: U): T & U;
export declare function __assign<T, U, V>(target: T, source1: U, source2: V): T & U & V;
export declare function __assign<T, U, V, W>(target: T, source1: U, source2: V, source3: W): T & U & V & W;
export declare function __assign(target: object, ...sources: any[]): any;

/**
 * Creates an async iterator delegator for async generator functions.
 * @param iterable - The async iterable to delegate to
 * @returns An async iterator with next, throw, and return methods
 */
export declare function __asyncDelegator(iterable: AsyncIterable<any>): AsyncIterator<any>;

/**
 * Creates an async generator wrapper with promise-based iteration.
 * @param thisArg - The context object (this binding)
 * @param argumentsList - Arguments to pass to the generator function
 * @param generator - The generator function to wrap
 * @returns An async iterator
 */
export declare function __asyncGenerator(
    thisArg: any,
    argumentsList: any[],
    generator: Function
): AsyncIterator<any>;

/**
 * Converts an async iterable to an async iterator.
 * @param iterable - The async iterable object
 * @returns An async iterator with next, throw, and return methods
 */
export declare function __asyncValues<T>(iterable: AsyncIterable<T> | Iterable<T>): AsyncIterator<T>;

/**
 * Wraps a value to be awaited in async generator functions.
 * @param value - The value to await
 * @returns A wrapped awaitable value
 */
export declare function __await<T>(value: T): T;

/**
 * Wraps async functions to return promises.
 * @param thisArg - The context object (this binding)
 * @param argumentsList - Arguments to pass to the async function
 * @param PromiseConstructor - The Promise constructor to use
 * @param generator - The generator function representing the async body
 * @returns A promise resolving to the async function result
 */
export declare function __awaiter<T>(
    thisArg: any,
    argumentsList: any[] | undefined,
    PromiseConstructor: PromiseConstructorLike,
    generator: (...args: any[]) => Iterator<any, T>
): Promise<T>;

/**
 * Reads a private class field (getter).
 * @param receiver - The object instance
 * @param state - The WeakMap or private field state
 * @param kind - The kind of access: "f" (field), "a" (accessor), "m" (method)
 * @param getter - The getter function for accessor
 * @returns The private field value
 * @throws TypeError if private member is not accessible
 */
export declare function __classPrivateFieldGet<T, V>(
    receiver: T,
    state: { has(o: T): boolean; get(o: T): V },
    kind: "f" | "a" | "m",
    getter?: (this: T) => V
): V;

/**
 * Checks if an object has a private field.
 * @param state - The WeakMap or constructor function
 * @param receiver - The object to check
 * @returns True if the private field exists
 * @throws TypeError if receiver is not an object
 */
export declare function __classPrivateFieldIn(
    state: Function | { has(o: any): boolean },
    receiver: any
): boolean;

/**
 * Sets a private class field (setter).
 * @param receiver - The object instance
 * @param state - The WeakMap or private field state
 * @param value - The value to set
 * @param kind - The kind of access: "f" (field), "a" (accessor), "m" (method)
 * @param setter - The setter function for accessor
 * @returns The set value
 * @throws TypeError if private member is not writable
 */
export declare function __classPrivateFieldSet<T, V>(
    receiver: T,
    state: { has(o: T): boolean; set(o: T, value: V): void },
    value: V,
    kind: "f" | "a" | "m",
    setter?: (this: T, value: V) => void
): V;

/**
 * Creates a binding for re-exported module members.
 * @param target - The target module exports object
 * @param source - The source module exports object
 * @param key - The key to bind from source
 * @param alias - Optional alias name in target (defaults to key)
 */
export declare function __createBinding(
    target: object,
    source: object,
    key: PropertyKey,
    alias?: PropertyKey
): void;

/**
 * Applies decorators to a class, method, accessor, or property.
 * @param decorators - Array of decorator functions
 * @param target - The target class or prototype
 * @param propertyKey - The property key (for member decorators)
 * @param descriptor - The property descriptor (for method/accessor decorators)
 * @returns The modified descriptor or undefined
 */
export declare function __decorate(
    decorators: Function[],
    target: any,
    propertyKey?: string | symbol,
    descriptor?: PropertyDescriptor | null
): PropertyDescriptor | undefined;

/**
 * Re-exports all members from a module except 'default'.
 * @param source - The source module to export from
 * @param target - The target exports object
 */
export declare function __exportStar(source: object, target: object): void;

/**
 * Sets up prototype chain for class inheritance.
 * @param derived - The derived (child) class constructor
 * @param base - The base (parent) class constructor
 * @throws TypeError if base is not a constructor or null
 */
export declare function __extends(derived: Function, base: Function): void;

/**
 * Creates a generator state machine for generator functions.
 * @param thisArg - The context object (this binding)
 * @param body - The generator body function
 * @returns An iterator with next, throw, and return methods
 */
export declare function __generator(
    thisArg: any,
    body: (state: any) => any
): Iterator<any>;

/**
 * Returns the default export or wraps the module in a default property.
 * @param module - The imported module
 * @returns The module with __esModule marker
 */
export declare function __importDefault<T>(module: T): T & { default: T };

/**
 * Imports all exports from a module as a namespace object.
 * @param module - The imported module
 * @returns A namespace object with all exports
 */
export declare function __importStar<T>(module: T): T;

/**
 * Creates a template object for tagged template literals.
 * @param cooked - The array of cooked string values
 * @param raw - The array of raw string values
 * @returns The template object with raw property
 */
export declare function __makeTemplateObject(
    cooked: TemplateStringsArray,
    raw: TemplateStringsArray
): TemplateStringsArray;

/**
 * Emits metadata for decorators (requires reflect-metadata).
 * @param metadataKey - The metadata key
 * @param metadataValue - The metadata value
 * @returns A decorator function or undefined
 */
export declare function __metadata(
    metadataKey: any,
    metadataValue: any
): ((target: Function) => void) | ((target: object, propertyKey: string | symbol) => void) | undefined;

/**
 * Creates a parameter decorator.
 * @param parameterIndex - The index of the parameter
 * @param decorator - The decorator function
 * @returns A parameter decorator function
 */
export declare function __param(
    parameterIndex: number,
    decorator: (target: any, propertyKey: string | symbol, parameterIndex: number) => void
): (target: any, propertyKey: string | symbol) => void;

/**
 * Converts an iterable to an array with a specified length.
 * @param iterable - The iterable to read from
 * @param length - Optional maximum number of elements to read
 * @returns An array of elements
 */
export declare function __read<T>(iterable: Iterable<T>): T[];
export declare function __read<T>(iterable: Iterable<T>, length: number): T[];

/**
 * Extracts remaining properties from an object (object rest).
 * @param source - The source object
 * @param excludedKeys - Keys to exclude from the result
 * @returns A new object without the excluded keys
 */
export declare function __rest<T, K extends keyof T>(
    source: T,
    excludedKeys: K[]
): Omit<T, K>;

/**
 * Spreads arrays into a single array (legacy, use __spreadArray).
 * @param arrays - Arrays to spread
 * @returns A single concatenated array
 * @deprecated Use __spreadArray instead
 */
export declare function __spread<T>(...arrays: (T | T[])[]): T[];

/**
 * Spreads or concatenates arrays efficiently.
 * @param target - The target array to prepend
 * @param source - The source array to spread
 * @param pack - Whether to pack the result (affects performance)
 * @returns A new concatenated array
 */
export declare function __spreadArray<T>(
    target: T[],
    source: T[],
    pack?: boolean
): T[];

/**
 * Spreads multiple arrays into a single array (legacy).
 * @param arrays - Arrays to spread
 * @returns A single concatenated array
 * @deprecated Use __spreadArray instead
 */
export declare function __spreadArrays<T>(...arrays: (T | T[])[]): T[];

/**
 * Converts an iterable or array-like object to an iterator.
 * @param iterable - The iterable or array-like object
 * @returns An iterator with next method
 * @throws TypeError if object is not iterable
 */
export declare function __values<T>(iterable: Iterable<T> | ArrayLike<T>): Iterator<T>;