/**
 * MDI (Material Design Icons) SVG path definitions for common UI icons.
 * Each property contains an SVG path string that can be used with the `<path>` element.
 * All paths are designed for a 24x24 viewBox.
 * 
 * @module MdiSvgIconPreset
 */

/**
 * Collection of Material Design Icons as SVG path strings.
 * These icons are commonly used throughout the UI for actions, states, and navigation.
 */
declare const mdiSvgIconPreset: {
  /** Checkmark icon indicating completion or success */
  complete: string;
  
  /** Cancel icon - circle with X, used to cancel operations */
  cancel: string;
  
  /** Close icon - simple X, used to close dialogs or dismiss items */
  close: string;
  
  /** Delete icon - circle with X, used for delete actions */
  delete: string;
  
  /** Clear icon - X mark, used to clear input fields or selections */
  clear: string;
  
  /** Success icon - circle with checkmark, indicates successful operation */
  success: string;
  
  /** Info icon - circle with 'i', used to display information */
  info: string;
  
  /** Warning icon - exclamation mark, indicates warning state */
  warning: string;
  
  /** Error icon - triangle with exclamation, indicates error state */
  error: string;
  
  /** Previous/back navigation arrow pointing left */
  prev: string;
  
  /** Next/forward navigation arrow pointing right */
  next: string;
  
  /** Checkbox in checked state */
  checkboxOn: string;
  
  /** Checkbox in unchecked state */
  checkboxOff: string;
  
  /** Checkbox in indeterminate state (dash/minus) */
  checkboxIndeterminate: string;
  
  /** Delimiter icon - filled circle, used as separator or bullet point */
  delimiter: string;
  
  /** Sort icon - up arrow, used for sorting functionality */
  sort: string;
  
  /** Expand icon - down chevron, used to expand collapsed content */
  expand: string;
  
  /** Menu icon - three horizontal lines (hamburger menu) */
  menu: string;
  
  /** Subgroup icon - down triangle, indicates nested/grouped content */
  subgroup: string;
  
  /** Dropdown icon - down triangle, indicates expandable dropdown */
  dropdown: string;
  
  /** Radio button in selected state */
  radioOn: string;
  
  /** Radio button in unselected state */
  radioOff: string;
  
  /** Edit icon - pencil, used for edit actions */
  edit: string;
  
  /** Rating star in empty state */
  ratingEmpty: string;
  
  /** Rating star in filled state */
  ratingFull: string;
  
  /** Rating star in half-filled state */
  ratingHalf: string;
  
  /** Loading/refresh icon - circular arrows, indicates loading state */
  loading: string;
  
  /** First page navigation icon - double left arrow */
  first: string;
  
  /** Last page navigation icon - double right arrow */
  last: string;
  
  /** Unfold icon - up and down arrows, used to expand/collapse all */
  unfold: string;
  
  /** File attachment icon - paperclip */
  file: string;
  
  /** Plus icon - used for add actions */
  plus: string;
  
  /** Minus icon - used for remove or minimize actions */
  minus: string;
};

export default mdiSvgIconPreset;